'use strict';

var twemoji = require('twemoji')

module.exports = function emoji_html(tokens, idx /*, options, env */) {
  if(window.navigator.platform == "Linux x86_64")
  {
    return twemoji.parse(tokens[idx].content, {
      size: '16x16'
    });
  }
  else{
    return tokens[idx].content;
  }
}
